import React from "react";
import './projects.css';
import { Card } from "react-bootstrap";
//import { CardGroup } from "react-bootstrap";
//import { Row } from "react-bootstrap";
//import { Col } from "react-bootstrap";

const Projects= () => {
    const cardInfo = [
        {image:'https://images.pexels.com/photos/3616759/pexels-photo-3616759.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940', title:'Drywall', text:'We fix it'},
        {image:'https://images.pexels.com/photos/3616759/pexels-photo-3616759.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940', title:'Paint', text:'We paint it'},
        {image:'https://images.pexels.com/photos/3616759/pexels-photo-3616759.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940', title:'Plaster', text:'We remove it'},
        {image:'https://images.pexels.com/photos/3616759/pexels-photo-3616759.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940', title:'Water Damage', text:'We fix it also'}
    ];

    const renderCards = (card, index) => {
        return (
            <Card style={{ width: '18rem' }} key={index} className='imageGrid'>
            <Card.Img variant="top" src="https://images.pexels.com/photos/4792532/pexels-photo-4792532.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940" src={card.image}/>
                <Card.Body>
                    <Card.Title>{card.title}</Card.Title>
                        <Card.Text>
                            {card.text}
                        </Card.Text>
                </Card.Body>
        </Card>
        )
    };
    return(
        <div>
        {cardInfo.map(renderCards)}
        </div>
    )
}

export default Projects;
