import React from 'react';
import './about.css';

function About () {
    return(
        <div>
        <img id='wsImg' src='https://photos.letsroam.com/scavenger_hunt_locations/Scavenger_Hunt_Winston_Salem_City_Center_Winston%E2%80%93Salem_North_Carolina_team_building_1528907578_large.jpg'
        alt='wsnc'
        />
        <h4>
            Established in 2002. We can use the object-fit property in CSS to resize the image to fit its container. A container may be larger or smaller in size than the image. The property lets us fit the image or videos according to the size of the container. We can specify the way how the image fits using the object-fit property. The property takes the values like fill, contain, cover, none, and scale-down. We can use the contain value so that the larger image can shrink to the given dimension of the container.
        </h4>
        </div>
    )
}

export default About;
